import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import houseService from '../../services/houseService'

function UpdateHouse(data) {
    const [dataId, setDataId] = useState();
    const [formData, setFormData] = useState({...data});

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.name]: event.target.value
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        async function fetchData() {
            const data = await houseService.addHouse(formData);
            if (data?.id) setDataId(data.id)
        }
        fetchData();
    };

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="address">Address:</label>
            <input required type="text" id="address" name="address" value={data.address} onChange={handleChange} />

            <label htmlFor="currentValue">Current Value:</label>
            <input required type="number" id="currentValue" name="currentValue" value={data.currentValue} onChange={handleChange} />

            <label htmlFor="loanAmount">Loan Amount:</label>
            <input required type="number" id="loanAmount" name="loanAmount" value={data.loanAmount} onChange={handleChange} />

            <button type="submit">Submit</button>

            {dataId && <> {dataId}
                <Link to={`/house/${dataId}`}>
                    <button>show ditails</button>
                </Link>
            </>}
        </form>
    );
}

export default UpdateHouse;
